using UnityEngine;
using System.Collections;

public class SceneLoadData{
    private string _destination;
    private string _source;

    // Auto-generated functions to allow the private member be treated like public
    public string destination {
        get {
            return _destination;
        }

        set {
            _destination = value;
        }
    }

    public string source {
        get {
            return _source;
        }

        set {
            _source = value;
        }
    }
}
